function article(coverID, title, content) {
	this.coverID = coverID;
	this.title = title;
	this.content = content;
}
function chapter(name, element) {
	this.name = name;
	this.element = element;
	this.articleList = [];
}
chapter.prototype.addArticle = function(coverID, title, content) {
	var a = new article(coverID, title, content);
	this.articleList.push(a);
};
function gotoPage(section, page, totalPage) {
	for(var i = 1; i <= totalPage; i++) {
		document.getElementById(section + "-page-" + i).style.display = "none";
	}
	document.getElementById(section + "-page-" + page).style.display = "block";
	var p = document.getElementById(section + "-paging").children;
	for(var i = 0; i < p.length; i++)
		p[i].className = "";
	p[page - 1].className = "active";
}


